/* تغییر رنگ نوار اسکرول */
::-webkit-scrollbar {
    width: 9px; /* عرض نوار اسکرول */
}

::-webkit-scrollbar-thumb {
    background-color: #F0A400; /* رنگ نوار اسکرول */
    border-radius: 2px; /* گوشه‌های گرد */
}

::-webkit-scrollbar-track {
    background-color: #212121; /* رنگ پس‌زمینه نوار اسکرول */
}
