function custom_admin_dark_mode_full() {
    echo '
    <style>
        /* --- عمومی پنل مدیریت --- */
        body, #wpadminbar, #adminmenu, #adminmenu .wp-submenu, .wrap, .wp-core-ui {
            background-color: #1e1e1e !important;
            color: #d0d0d0 !important;
        }

        #adminmenu a, #adminmenu .wp-submenu a, .wp-core-ui select, .wp-core-ui input, .wp-core-ui textarea {
            color: #d0d0d0 !important;
        }

        #adminmenu .wp-submenu a:hover, #adminmenu a:hover,
        #wpadminbar .ab-top-menu > li.hover > .ab-item {
            background-color: #333 !important;
            color: #fff !important;
        }

        .postbox, .metabox-holder, .stuffbox, .inside {
            background-color: #2b2b2b !important;
            color: #ddd !important;
            border-color: #444 !important;
        }

        .postbox h2, .postbox h3, .postbox h1, .hndle {
            color: #fff !important;
        }

        .edit-post-meta-boxes-area, .editor-post-title, .components-panel, .editor-post-settings__panel, .editor-post-publish-panel {
            background-color: #222 !important;
            color: #ccc !important;
        }

        .components-button, .button, .button-primary, .button-secondary {
            background-color: #444 !important;
            border-color: #666 !important;
            color: #fff !important;
        }

        .components-button:hover, .button:hover, .button-primary:hover, .button-secondary:hover {
            background-color: #555 !important;
            border-color: #888 !important;
            color: #fff !important;
        }

        textarea, input, select {
            background-color: #2a2a2a !important;
            color: #fff !important;
            border: 1px solid #555 !important;
        }

        .wp-editor-container textarea.wp-editor-area {
            background-color: #1c1c1c !important;
            color: #eee !important;
        }

        .wp-core-ui .notice, .update-nag {
            background-color: #333 !important;
            color: #f0ad4e !important;
        }

        /* --- رنک مث (Rank Math) --- */
        .rank-math-metabox, .rank-math-metabox .inside {
            background-color: #2b2b2b !important;
            color: #ccc !important;
            border-color: #444 !important;
        }

        .rank-math-metabox .rank-math-label {
            color: #fff !important;
        }

        .rank-math-metabox a, .rank-math-metabox a:hover {
            color: #80b3ff !important;
        }

        .rank-math-notices {
            background-color: #333 !important;
            color: #f0ad4e !important;
        }

        /* --- کد اسنیپت / Structured Data --- */
        .schema-settings, .snippet-box {
            background-color: #2b2b2b !important;
            color: #ddd !important;
            border-color: #444 !important;
        }

        .schema-settings input, .snippet-box input, .schema-settings textarea, .snippet-box textarea {
            background-color: #1e1e1e !important;
            color: #eee !important;
            border: 1px solid #555 !important;
        }

        /* --- ووکامرس (WooCommerce) --- */
        .woocommerce, .woocommerce-page {
            background-color: #1e1e1e !important;
            color: #d0d0d0 !important;
        }

        .woocommerce .postbox, .woocommerce-page .postbox {
            background-color: #2b2b2b !important;
            color: #ccc !important;
            border-color: #444 !important;
        }

        .woocommerce table.wp-list-table {
            background-color: #1e1e1e !important;
            color: #ddd !important;
        }

        .woocommerce table.wp-list-table th, 
        .woocommerce table.wp-list-table td {
            border-color: #444 !important;
        }

        .woocommerce input[type="text"],
        .woocommerce input[type="number"],
        .woocommerce select,
        .woocommerce textarea {
            background-color: #2a2a2a !important;
            color: #eee !important;
            border: 1px solid #555 !important;
        }

        .woocommerce .button, .woocommerce-page .button {
            background-color: #444 !important;
            border-color: #666 !important;
            color: #fff !important;
        }

        .woocommerce .button:hover, .woocommerce-page .button:hover {
            background-color: #555 !important;
            border-color: #888 !important;
        }
    </style>
    ';
}
add_action('admin_head', 'custom_admin_dark_mode_full');
